# Created by Isaac Hinckley (000636849)import sysfrom src.classes.SoSLocations import SoSLocationsfrom src.classes.SoSPackages import SoSPackagesfrom src.classes.SoSTruck import SoSTruckfrom src.classes.SoSTrucks import SoSTrucks# Simulation steps.def simulate_deliveries():    # Import and set up location data.    SoSLocations.initialize()    # Import and set up package data.    SoSPackages.initialize()    # Initialize department trucks    truck_1: SoSTruck = SoSTrucks.truck_1    truck_2: SoSTruck = SoSTrucks.truck_2    # Only two drivers showed up for work today. Truck 3 will not be used.    truck_3: SoSTruck = SoSTrucks.truck_3    # Perform work until packages are gone.    while SoSPackages.packages_at_hub > 0:        # No use of asynchronous systems. Simulation will drive one truck at a time.        active_truck: SoSTruck = determine_which_truck(truck_1, truck_2)        # Load the active truck.        active_truck.load_truck()        # Send the truck to work.        active_truck.perform_deliveries()# Checks the truck clocks against each other. Whichever truck has the earliest time will be the designated active truck.# Defaults to truck 1 if both clocks match.def determine_which_truck(truck_1, truck_2):    return truck_1 if truck_1.get_time() <= truck_2.get_time() else truck_2def pick_option():    print('Pick option (any other input assumes option 1)')def end_of_day():    print('Total miles travelled: ', SoSTrucks.truck_1.get_miles_traveled()+SoSTrucks.truck_2.get_miles_traveled())    pick_option()    print('1 - All packages')    print('2 - Specific package')    print('3 - Back')    print('0 - Quit')    user_input = input('Option: ')    match user_input:        case '2':            package_to_query = input('Enter package ID: ')            package = SoSPackages.packages.get(package_to_query)            if package != -1:                print('Package ', package.get_id(), ': ', package.get_delivery_status_readable(), ' at ', package.get_delivered_time().time())        case '3':            user_interface()        case '0':            sys.exit('Goodbye')        case _:            for package in SoSPackages.packages:                print('Package ', package.get_id(), ': ', package.get_delivery_status_readable(), ' at ', package.get_delivered_time().time())def user_interface():    pick_option()    print('1 - End of day data')    print('2 - Specific package at time')    print('3 - Packages en route at given time')    print('0 - Quit')    user_input = input('Option: ')    match user_input:        case '0':            sys.exit('Goodbye')        case '2':            print('picked 2')        case '3':            print('picked 3')        case _:            end_of_day()simulate_deliveries()user_interface()